                    var selectedAppDate = $("#rps_dateandtimeofreservation_datepicker_description").datepicker({ dateFormat: 'yyyy-MM-dd' }).val();
                    selectedAppDate = new Date(selectedAppDate);
                    var currentDateNow = new Date();
                    currentDateNow.setHours(currentDateNow.getHours() + 24); // Ensure we compare 24 hours ahead

                    var errorSpanDate = $('#dateError');
                    if (errorSpanDate.length === 0) {
                        // Create the date error span and insert it directly after the date picker description
                        errorSpanDate = $('<span id="dateError" class="alert alert-danger" style="display: none; margin-top: 5px; padding: 2px; font-size: smaller;"></span>');
                        $("#rps_dateandtimeofreservation_datepicker_description").after(errorSpanDate);
                    }

                    var phone = $("#rps_phonenumber").val();
                    var errorSpanPhone = $('#phoneError');
                    if (errorSpanPhone.length === 0) {
                        // Create the phone error span and insert it directly after the phone number input
                        errorSpanPhone = $('<span id="phoneError" class="alert alert-danger" style="display: none; margin-top: 5px; padding: 2px; font-size: smaller;"></span>');
                        $("#rps_phonenumber").after(errorSpanPhone);
                    }

                    var validDate = true, validPhone = true;

                    if(selectedAppDate < currentDateNow) {
                        errorSpanDate.text("Reservations must be made at least 24 hours in advance.");
                        errorSpanDate.show();
                        validDate = false;
                    } else {
                        errorSpanDate.hide();
                    }

                    if(!/^\d{10}$/.test(phone)) {
                        errorSpanPhone.text("Phone number must be exactly 10 digits.");
                        errorSpanPhone.show();
                        validPhone = false;
                    } else {
                        errorSpanPhone.hide();
                    }

                    return validDate && validPhone; // form is valid only if both validations pass
